// 1) 테이블 생성하기
CREATE TABLE flights(
    DEST_COUNTRY_NAME STRING,
    ORIGIN_COUNTRY_NAME STRING COMMENT "remember, the US will be the most prevalent",   // 코멘트 지정
    count LONG)
USING csv OPTIONS(header true,  path '/data/flight-data/csv/2015-summary.csv')

// 3) 테이블에 데이터 삽입
INSERT INTO flights_select
    SELECT DEST_COUNTRY_NAME,ORIGIN_COUNTRY_NAME,count FROM flights LIMIT 20

// 4) 테이블의 메타데이터 확인하기

// DESCRIBE : 메타데이터 정보 반환
DESCRIBE TABLE flights

// SHOW : 파티셔닝 스키마 정보 확인.
SHWO PARTITIONS partitioned_flights

// 메타 데이터 갱신하기
REFRESH TABLE partitioned_flights

// 카탈로그에서 관리하는 파티션 정보를 새로 고치는 메서드
MSCK REPAIR TABLE partitioned_flights

// 5) 테이블 제거 
// 테이블 삭제는 불가능 오로지 제거만 가능
DROP TABLE flights;
DROP TABLE IF EXISTS flights;

// 외부 테이블 제거
// DataFrame에서 테이블 캐시하거나
CACHE TABLE flights
// 캐시에서 제거 가능
UNCACHE TABLE FLIGHTS

// 1) 뷰 생성
// 쿼리 시점에 데이터 소스에 트랜스포메이션 수행 : CREATE VIEW
CREATE VIEW just_usa_view AS
    SELECT * FROM flights WHERE DEST_COUNTRY_NAME="United States"

// 테이블처럼 데이터베이스에 등록되지 않고 현재 세션에서만 사용할 수 있는 임시뷰 생성 : CREATE TEMP
CREATE TEMP just_usa_view AS
    SELECT * FROM flights WHERE DEST_COUNTRY_NAME="United States"

// 전역 임시 뷰.데이터베이스에 상관 없이 사용 가능. 전체 스파크 어플리케이션에서 볼 수 있으나, 세션이 끝나면 뷰도 사라짐 : CREATE GLOBAL TEMP VIEW
CREATE GLOBAL TEMP VIEW just_usa_view AS
    SELECT * FROM flights WHERE DEST_COUNTRY_NAME="United States"

